INSERT INTO `base_gallery` VALUES (16,'The Netherlands','\r\n    \r\n    \r\n	    \r\n	    is a constituent country of the Kingdom of the Netherlands, located mainly in North-West Europe and with some islands in the Caribbean.<br>','en',1),(17,'Spain','is a sovereign state and a member of the European Union located in southwestern Europe on the Iberian Peninsula.','en',2),(18,'Italy','\r\n	    \r\n	    \r\n	    \r\n	    is a unitary parliamentary republic in South-Central Europe. To the \r\nnorth, it borders France, Switzerland, Austria, and Slovenia along the \r\nAlps. To the south, it consists of the untirety of the Italian \r\nPeninsula, Sicily, Sardinia–the two largest islands in the Mediterranean\r\n Sea–and many other smaller islands.					','en',3),(232,'Испания','является суверенным государством и членом Европейского Союза на юго-западе Европы, на Пиренейском полуострове.','ru',2),(233,'Іспанія','є суверенною державою і членом Європейського Союзу на південному заході Європи, на Піренейському півострові.','ua',2),(234,'Espagne','est un Etat souverain et un membre de l&#39;Union européenne situé dans le sud-ouest l&#39;Europe sur la péninsule ibérique.','fr',2),(235,'Spanien','ist ein souveräner Staat und Mitglied der Europäischen Union im Südwesten Europas auf der iberischen Halbinsel.','de',2),(236,'España','es un Estado soberano y miembro de la Unión Europea situado en el suroeste de Europa en la Península Ibérica.','es',2),(237,'Spagna','è uno Stato sovrano e membro dell&#39;Unione Europea situato nell&#39;Europa sud-occidentale della penisola iberica.','it',2),(238,'Ισπανία','είναι ένα κυρίαρχο κράτος και μέλος της Ευρωπαϊκής Ένωσης, που βρίσκεται στη νοτιοδυτική Ευρώπη για την Ιβηρική Χερσόνησο.','gr',2),(239,'西班牙','是一个主权国家，位于欧洲西南部伊比利亚半岛上的欧洲联盟的成员。','ch',2),(240,'スペイン','主権国家とイベリア半島南西ヨーロッパに位置する欧州連合の一員です。','jp',2),(241,'İspanya','egemen bir devlet ve İber Yarımadası&#39;nda güneybatı Avrupa&#39;da bulunan, Avrupa Birliği&#39;nin bir üyesidir.','tr',2),(242,'